-- Minimize button functionality
local minimized = false
minimizeBtn.MouseButton1Click:Connect(function()
    if minimized then
        TweenService:Create(mainFrame, TweenInfo.new(0.3), {Size = UDim2.new(0, mainFrame.Size.X.Offset, 0, 1000)}):Play()
        TweenService:Create(scrollFrame, TweenInfo.new(0.3), {BackgroundTransparency = 0}):Play()
        minimized = false
    else
        TweenService:Create(mainFrame, TweenInfo.new(0.3), {Size = UDim2.new(0, mainFrame.Size.X.Offset, 0, 45)}):Play()
        TweenService:Create(scrollFrame, TweenInfo.new(0.3), {BackgroundTransparency = 1}):Play()
        minimized = true
    end
end)

